#include <iostream>#include <string>using namespace std;class Student{private:	string name;	char sex;	int age;public:	Student(string n = "null", char s = 'F', int a = 10):name(n), sex(s), age(a){}	void show_info()	{		cout << "name = " << name << endl;		cout << "sex = " << sex << endl;		cout << "age = " << age << endl;	}};int main(int argc, char const *argv[]){	cout << "this is s1" << endl;	Student s1("xiong", 'F', 21);	s1.show_info(); 	cout << endl <<"now use s2 = s1" << endl << endl;	Student s2 = s1;//对象之间的赋值只包含成员变量，不包括函数，同一类的所有对象使用同一内存空间存储函数	s2.show_info();	cout << endl << "对象的复制" << endl;	cout << "------------------------\n";	Student s3(s1);	s3.show_info();	return 0;}